mapper.select:
select id, number, status, user_id, address_book_id, order_time, checkout_time, pay_method, pay_status, amount, remark, phone, address, user_name, consignee, cancel_reason, rejection_reason, cancel_time, estimated_delivery_time, delivery_status, delivery_time, pack_amount, tableware_number, tableware_status
from orders
where user_id = #{userId}
and status = #{status}
order by order_time desc

select id, number, status, user_id, address_book_id, order_time, checkout_time, pay_method, pay_status, amount, remark, phone, address, user_name, consignee, cancel_reason, rejection_reason, cancel_time, estimated_delivery_time, delivery_status, delivery_time, pack_amount, tableware_number, tableware_status from orders
and number like concat("%",#{number},"%") and phone like concat("%",#{phone},"%") and status = #{status} and begin_time >= #{beginTime} and end_time <= #{endTime}
order by order_time desc
