mapper.select:
select id,name,price,status,category_id,image,description,create_time,create_user,update_time,update_user
from setmeal

    and id = #{id}
    and name = #{name}
    and status = #{status}
    and category_id = #{categoryId}

select id,category_id,name,price,status,description,image,update_time,create_time from setmeal

    and name like concat("%",#{name},"%")
    and category_id = #{categoryId}
    and status = #{status}

order by create_time desc
